mapper.select:
SELECT

FROM auth_permission



            AND id = #{param.id}


            AND name = #{param.name}


            AND parent_id = #{param.parentId}


            AND type = #{param.type}


            AND menu_url = #{param.menuUrl}


            AND status = #{param.status}


            AND show = #{param.show}


            AND icon = #{param.icon}


            AND permission_key = #{param.permissionKey}


            AND created_by = #{param.createdBy}


            AND created_time = #{param.createdTime}


            AND update_by = #{param.updateBy}


            AND update_time = #{param.updateTime}


            AND is_deleted = #{param.isDeleted}



ORDER BY id DESC

    LIMIT #{offset}, #{limit}

