mapper.select:
SELECT
id, role_name, role_key, created_by, created_time, update_by, update_time, is_deleted
FROM auth_role



            AND id = #{param.id}


            AND role_name = #{param.roleName}


            AND role_key = #{param.roleKey}


            AND created_by = #{param.createdBy}


            AND created_time = #{param.createdTime}


            AND update_by = #{param.updateBy}


            AND update_time = #{param.updateTime}


            AND is_deleted = #{param.isDeleted}



ORDER BY id DESC

    LIMIT #{offset}, #{limit}

SELECT
id, role_name, role_key, created_by, created_time, update_by, update_time, is_deleted
FROM auth_role



            AND id = #{param.id}


            AND role_name = #{param.roleName}


            AND role_key = #{param.roleKey}


            AND created_by = #{param.createdBy}


            AND created_time = #{param.createdTime}


            AND update_by = #{param.updateBy}


            AND update_time = #{param.updateTime}


            AND is_deleted = #{param.isDeleted}



ORDER BY id DESC

mapper.delete:
DELETE FROM auth_role
WHERE id IN

    #{id}

mapper.select:
SELECT COUNT(*) FROM auth_role



            AND id = #{param.id}


            AND role_name = #{param.roleName}


            AND role_key = #{param.roleKey}


            AND created_by = #{param.createdBy}


            AND created_time = #{param.createdTime}


            AND update_by = #{param.updateBy}


            AND update_time = #{param.updateTime}


            AND is_deleted = #{param.isDeleted}

SELECT COUNT(*) > 0
FROM auth_role
WHERE id = #{id}
