mapper.select:
SELECT
id, user_name, nick_name, email, phone, password, sex, avatar, status, introduce, ext_json, created_by, created_time, update_by, update_time, is_deleted
FROM auth_user




            AND id = #{param.id}



            AND user_name LIKE CONCAT('%', #{param.userName}, '%')



            AND nick_name LIKE CONCAT('%', #{param.nickName}, '%')



            AND email LIKE CONCAT('%', #{param.email}, '%')



            AND phone = #{param.phone}



            AND sex = #{param.sex}


            AND status = #{param.status}


            AND is_deleted = #{param.isDeleted}



ORDER BY id DESC

    LIMIT #{offset}, #{limit}

SELECT
id, user_name, nick_name, email, phone, password, sex, avatar, status, introduce, ext_json, created_by, created_time, update_by, update_time, is_deleted
FROM auth_user



            AND id = #{param.id}


            AND user_name = #{param.userName}


            AND nick_name = #{param.nickName}


            AND email = #{param.email}


            AND phone = #{param.phone}


            AND password = #{param.password}


            AND sex = #{param.sex}


            AND avatar = #{param.avatar}


            AND status = #{param.status}


            AND introduce = #{param.introduce}


            AND ext_json = #{param.extJson}


            AND created_by = #{param.createdBy}


            AND created_time = #{param.createdTime}


            AND update_by = #{param.updateBy}


            AND update_time = #{param.updateTime}


            AND is_deleted = #{param.isDeleted}



ORDER BY id DESC

