mapper.delete:
DELETE FROM share_circle
WHERE id IN

    #{id}

mapper.select:
SELECT COUNT(*) FROM share_circle



            AND id = #{param.id}


            AND parent_id = #{param.parentId}


            AND circle_name = #{param.circleName}


            AND icon = #{param.icon}


            AND created_by = #{param.createdBy}


            AND created_time = #{param.createdTime}


            AND update_by = #{param.updateBy}


            AND update_time = #{param.updateTime}


            AND is_deleted = #{param.isDeleted}

SELECT COUNT(*) > 0
FROM share_circle
WHERE id = #{id}
