mapper.delete:
DELETE FROM share_moment
WHERE id IN

    #{id}

mapper.select:
SELECT COUNT(*) FROM share_moment



            AND id = #{param.id}


            AND circle_id = #{param.circleId}


            AND content = #{param.content}


            AND pic_urls = #{param.picUrls}


            AND reply_count = #{param.replyCount}


            AND created_by = #{param.createdBy}


            AND created_time = #{param.createdTime}


            AND update_by = #{param.updateBy}


            AND update_time = #{param.updateTime}


            AND is_deleted = #{param.isDeleted}

SELECT COUNT(*) > 0
FROM share_moment
WHERE id = #{id}
