    #{id}

mapper.select:
SELECT COUNT(*) FROM subject_category



            AND id = #{param.id}


            AND category_name LIKE CONCAT('%', #{param.categoryName}, '%')


            AND category_type = #{param.categoryType}


            AND image_url = #{param.imageUrl}


            AND parent_id = #{param.parentId}


            AND created_by = #{param.createdBy}


            AND created_time = #{param.createdTime}


            AND update_by = #{param.updateBy}


            AND update_time = #{param.updateTime}


            AND is_deleted = #{param.isDeleted}

SELECT COUNT(*) > 0
FROM subject_category
WHERE id = #{id}

SELECT

FROM subject_category

    is_deleted = 0

        AND id = #{id}


        AND category_name = #{categoryName}


        AND category_type = #{categoryType}


        AND image_url = #{imageUrl}



        AND parent_id = #{parentId}


        AND created_by = #{createdBy}


        AND created_time = #{createdTime}


        AND update_by = #{updateBy}


        AND update_time = #{updateTime}


        AND is_deleted = #{isDeleted}


ORDER BY id ASC
