mapper.select:
SELECT COUNT(*) FROM subject_mapping



            AND id = #{param.id}


            AND subject_id = #{param.subjectId}


            AND category_id = #{param.categoryId}


            AND label_id = #{param.labelId}


            AND created_by = #{param.createdBy}


            AND created_time = #{param.createdTime}


            AND update_by = #{param.updateBy}


            AND update_time = #{param.updateTime}


            AND is_deleted = #{param.isDeleted}

SELECT COUNT(*) > 0
FROM subject_mapping
WHERE id = #{id}

SELECT

FROM subject_mapping


        AND subject_id = #{subjectId}


        AND category_id = #{categoryId}


        AND label_id = #{labelId}

    AND is_deleted = 0

ORDER BY id DESC

select
distinct label_id
from subject_mapping


        and id = #{id}


        and subject_id = #{subjectId}


        and category_id = #{categoryId}


        and label_id = #{labelId}


        and created_by = #{createdBy}


        and created_time = #{createdTime}


        and update_by = #{updateBy}


        and update_time = #{updateTime}


        and is_deleted = #{isDeleted}
