mapper.select:
select

from subject_record


        and id = #{subjectRecord.id}


        and user_id = #{subjectRecord.userId}


        and subject_id = #{subjectRecord.subjectId}


        and user_answer = #{subjectRecord.userAnswer}


        and is_correct = #{subjectRecord.isCorrect}


        and answer_time = #{subjectRecord.answerTime}


        and time_cost = #{subjectRecord.timeCost}


        and score = #{subjectRecord.score}


        and create_time = #{subjectRecord.createTime}


        and update_time = #{subjectRecord.updateTime}


limit #{offset}, #{pageSize}

select count(*)
from subject_record


        and id = #{id}


        and user_id = #{userId}


        and subject_id = #{subjectId}


        and user_answer = #{userAnswer}


        and is_correct = #{isCorrect}


        and answer_time = #{answerTime}


        and time_cost = #{timeCost}


        and score = #{score}


        and create_time = #{createTime}


        and update_time = #{updateTime}

SELECT

FROM subject_record
WHERE user_id = #{userId}
AND answer_time >= #{startTime}
AND answer_time < #{endTime}
ORDER BY answer_time DESC

SELECT COUNT(DISTINCT subject_id)
FROM subject_record
WHERE user_id = #{userId}
