mapper.select:
SELECT

FROM interview_history



            AND id = #{param.id}


            AND avg_score = #{param.avgScore}


            AND key_words = #{param.keyWords}


            AND tip = #{param.tip}


            AND interview_url = #{param.interviewUrl}


            AND created_by = #{param.createdBy}


            AND created_time = #{param.createdTime}


            AND update_by = #{param.updateBy}


            AND update_time = #{param.updateTime}


            AND is_deleted = #{param.isDeleted}



ORDER BY id DESC

    LIMIT #{offset}, #{limit}

