mapper.select:
SELECT COUNT(*) FROM practice_set



            AND id = #{param.id}


            AND set_name = #{param.setName}


            AND set_type = #{param.setType}


            AND set_heat = #{param.setHeat}


            AND set_desc = #{param.setDesc}


            AND primary_category_id = #{param.primaryCategoryId}


            AND created_by = #{param.createdBy}


            AND created_time = #{param.createdTime}


            AND update_by = #{param.updateBy}


            AND update_time = #{param.updateTime}


            AND is_deleted = #{param.isDeleted}

SELECT COUNT(*) > 0
FROM practice_set
WHERE id = #{id}
