mapper.select:
SELECT COUNT(*) FROM share_comment_reply



            AND id = #{param.id}


            AND moment_id = #{param.momentId}


            AND reply_type = #{param.replyType}


            AND to_id = #{param.toId}


            AND to_user = #{param.toUser}


            AND to_user_author = #{param.toUserAuthor}


            AND reply_id = #{param.replyId}


            AND reply_user = #{param.replyUser}


            AND replay_author = #{param.replayAuthor}


            AND content = #{param.content}


            AND pic_urls = #{param.picUrls}


            AND parent_id = #{param.parentId}


            AND leaf_node = #{param.leafNode}


            AND children = #{param.children}


            AND root_node = #{param.rootNode}


            AND created_by = #{param.createdBy}


            AND created_time = #{param.createdTime}


            AND update_by = #{param.updateBy}


            AND update_time = #{param.updateTime}


            AND is_deleted = #{param.isDeleted}

SELECT COUNT(*) > 0
FROM share_comment_reply
WHERE id = #{id}
