mapper.select:
SELECT

FROM share_message



            AND id = #{param.id}


            AND from_id = #{param.fromId}


            AND to_id = #{param.toId}


            AND content = #{param.content}


            AND is_read = #{param.isRead}


            AND created_by = #{param.createdBy}


            AND created_time = #{param.createdTime}


            AND update_by = #{param.updateBy}


            AND update_time = #{param.updateTime}


            AND is_deleted = #{param.isDeleted}



ORDER BY id DESC

    LIMIT #{offset}, #{limit}

