mapper.select:
SELECT COUNT(*) FROM subject_info



            AND id = #{param.id}


            AND subject_name = #{param.subjectName}


            AND subject_difficult = #{param.subjectDifficult}


            AND settle_name = #{param.settleName}


            AND subject_type = #{param.subjectType}


            AND subject_score = #{param.subjectScore}


            AND subject_parse = #{param.subjectParse}


            AND created_by = #{param.createdBy}


            AND created_time = #{param.createdTime}


            AND update_by = #{param.updateBy}


            AND update_time = #{param.updateTime}


            AND is_deleted = #{param.isDeleted}

SELECT COUNT(*) > 0
FROM subject_info
WHERE id = #{id}
